package main

import (
	"text/template"
)

var packageTemplate = template.Must(template.New("").Parse(`// Code generated by go-localize; DO NOT EDIT.
// This file was generated by robots at
// {{ .Timestamp }}

package {{ .Package }}

import (
	"mimir/corelib/i18n"
)


var l = i18n.New("en", "en", localizations)

func GetWithLocale(locale string, key I18nKey, replacements ...*i18n.Replacements) string {
	return l.GetWithLocale(locale, string(key), replacements...)
}

type I18nKey string

const (
{{- range $key, $element := .Keys }}
	{{ $key }} = "{{ $element }}"
{{- end }}
)

var localizations = map[string]string{
{{- range $key, $element := .Localizations }}
	"{{ $key }}": "{{ $element }}",
{{- end }}
}
`,
))
